import { useState} from "react";
import CentralPane from "./components/layout/CentralPane";
import Row from "./components/layout/Row";
import Form from "./components/interface/Form";
import Select from 'react-select';
import makeAnimated from 'react-select/animated';

const fruitChoice = [
  {value: 'apple', label: 'apple'},
  {value: 'orange', label: 'orange'},
  {value: 'kale', label:'kale'}
];

function App() {

  const [chosen, setChosen] = useState([])

  const [fruit, setFruit] = useState("")
  const [result, setResult] = useState("")

  function handleChange(event) {
    console.log(event.target.value);
    setChosen(event.target.value)
}
  return (
    <CentralPane>
      <Select 
        options = {fruitChoice}
        placeholder = 'Select ingredient'
        onChange = {handleChange}
        isSearchable
        isMulti
        autoFocus
      />
      <Row>
        <Form fruit={fruit} setFruit={setFruit} setResult={setResult}/>
          { result.length === 0 ? "type ingredient" : result.map(function(result){
            return <div>{result}</div>
          })}
      </Row>
    </CentralPane>
  );
}

export default App;
