import { useState, useEffect} from "react";
import Select from 'react-select';
import AsyncSelect from 'react-select/async';
import makeAnimated from 'react-select/animated';

const fruitChoice = [
    {value: 'apple', label: 'apple'},
    {value: 'orange', label: 'orange'},
    {value: 'kale', label:'kale'}
  ];

function Form({setFruit, setResult, fruit}) {

    const [inputValue, setValue] = useState("");
    const [selectedValue, setSelectedValue] = useState(null);
    const handleInputChange = value => {
        setValue(value);
        console.log(inputValue);
        console.log(value);
    };
    const handleChangeOne = value => {setSelectedValue(value)};
    const loadOptions = (inputValue) => {
        return fetch("/fruits")
            .then(res => res.json())
            .then(res => {
                res['names'].filter((a) => a.label.includes(inputValue))
            });
        };

    useEffect(()=> {console.log(loadOptions())},[]);

    function handleSubmit(event) {
        event.preventDefault();
        setResult("");
        const post_options = {
            method: 'POST',
            headers: { 'Content-Type' : 'application/json'},
            body: JSON.stringify({ data: fruit })
        }

        fetch("/fruits", post_options)
            .then(res => res.json())
            .then(res => setResult(res['processed']))

    }    

    function handleChange(event) {
        setFruit(event.target.value)
    }

    return(
    <div>
        <Select 
        options = {fruitChoice}
        placeholder = 'Select ingredient'
        onChange = {setFruit}
        isSearchable
        isMulti
        autoFocus
        />
        <AsyncSelect
            cacheOptions
            defaultOptions
            value = {selectedValue}
            loadOptions = {loadOptions}
            onInputChange = {handleInputChange}
            onChange = {handleChangeOne}
            isMulti
            autoFocus
        />
        <form onSubmit={handleSubmit}>
            <input onChange={handleChange} type="text"/>
        </form>
    </div>)

}

export default Form;
