import { useState } from "react";
import AsyncSelect from 'react-select/async';
import makeAnimated from 'react-select/animated';

const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '1px dotted pink',
      color: state.isSelected ? 'yellow' : 'green',
      padding: 20,
      backgroundColor: state.isSelected ? 'green' : 'white'
    }),
    control: () => ({
      // none of react-select's styles are passed to <Control />
      width: 1000,
    }),
    singleValue: (provided, state) => {
      const opacity = state.isDisabled ? 0.5 : 1;
      const transition = 'opacity 300ms';

      return { ...provided, opacity, transition };
    }
  }

function Form({setFruit, setResult, fruit}) {

    const [inputValue, setValue] = useState("");

    const handleInputChange = value => {setValue(value)};

    const handleChange = value => {setFruit(value)};

    const loadOptions = () => {
        console.log(inputValue)
        return fetch("/fruits")
            .then(res => res.json())
            .then(res => res['names'].filter((a) => a.value.includes(inputValue)))
        };

    function handleSubmit(event) {
        console.log(fruit)
        event.preventDefault();
        setResult("");
        const post_options = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({ data: fruit })
        }

        fetch("/fruits", post_options)
            .then(res => res.json())
            .then(res => setResult(res['processed']))

    }    

    return(
    <div>
        <form onSubmit={handleSubmit}>
            <AsyncSelect
                cacheOptions
                defaultOptions
                value = {fruit}
                loadOptions = {loadOptions}
                onInputChange = {handleInputChange}
                onChange = {handleChange}
                isMulti
                autoFocus
                placeholder = "Type one or several ingredients"
                components = {{ DropdownIndicator:() => null, IndicatorSeparator:() => null }}
                openMenuOnFocus={false}
                openMenuOnClick={false}
                styles = {customStyles}
            />
        </form>
    </div>)

}

export default Form;
