import { useEffect, useState } from "react";
import AsyncSelect from 'react-select/async';
import makeAnimated from 'react-select/animated';
import styles from './Form.module.css';


const customStyles = {
    control: (provided, state) => ({
        ...provided,
        borderRadius: 32,
        border: state.isFocused ? '2px solid #22C55E' : '2px solid #86EFAC',
        boxShadow: state.isFocused ? 0 : 0,
        '&:hover': {
            border: state.isFocused ? '2px solid #22C55E' : '2px solid #86EFAC'
        }
    }),
  }

function Form({setFruit, setResult, fruit, inputValue, setValue, defaultValue}) {

    useEffect(() => {
        console.log(fruit);
        const post_options = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({ data: fruit })
        }

        fetch("/fruits", post_options)
            .then(res => res.json())
            .then(res => setResult(res['processed']))
    }, [fruit]);

    const handleInputChange = value => {setValue(value)};

    const handleChange = value => {setFruit(value)};

    const loadOptions = () => {
        console.log(inputValue)
        return fetch("/fruits")
            .then(res => res.json())
            .then(res => res['names'].filter( a => a.includes(inputValue)).map( a => ({
                value: a,
                label: a
            })))
        };


    function handleSubmit(event) {
        console.log(fruit)
        event.preventDefault();
        setResult("");
        const post_options = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({ data: fruit })
        }

        fetch("/fruits", post_options)
            .then(res => res.json())
            .then(res => setResult(res['processed']))

    }    

    return(
    <form onSubmit={handleSubmit}>
        <AsyncSelect
        cacheOptions
        defaultOptions
        value = {fruit}
        loadOptions = {loadOptions}
        onInputChange = {handleInputChange}
        onChange = {handleChange}
        isMulti
        autoFocus
        placeholder = "Type one or several ingredients"
        components = {{ DropdownIndicator:() => null, IndicatorSeparator:() => null }}
        openMenuOnFocus = {false}
        openMenuOnClick = {false}
        className = {styles.classAsync}
        styles = {customStyles}
        selectedValue = "lime"

        />
    </form>
    )     
}

export default Form;
