import { useState } from "react";
import AsyncSelect from 'react-select/async';
import makeAnimated from 'react-select/animated';

function Form({setFruit, setResult, fruit}) {

    const [inputValue, setValue] = useState("");

    const handleInputChange = value => {setValue(value)};

    const handleChangeOne = value => {setFruit(value)};

    const loadOptions = () => {
        return fetch("/fruits")
            .then(res => res.json())
            .then(res => res['names'].filter((a) => a.value.includes(inputValue)))
        };

    function handleSubmit(event) {
        console.log(fruit)
        event.preventDefault();
        setResult("");
        const post_options = {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify({ data: fruit })
        }

        fetch("/fruits", post_options)
            .then(res => res.json())
            .then(res => setResult(res['processed']))

    }    

    function handleChange(event) {
        setFruit(event.target.value)
    }

    return(
    <div>
        <form onSubmit={handleSubmit}>
            <AsyncSelect
                cacheOptions
                defaultOptions
                value = {fruit}
                loadOptions = {loadOptions}
                onInputChange = {handleInputChange}
                onChange = {handleChangeOne}
                isMulti
                autoFocus
            />
        </form>
    </div>)

}

export default Form;
