import { useState} from "react";
import Glass from "./components/layout/Glass";
import Form from "./components/interface/Form";
import Wrapper from "./components/layout/Wrapper";
import BubbleTop from "./components/layout/BubbleTop";
import BubbleBottom from "./components/layout/BubbleBottom";
import styles from "./App.module.css";
import Logo from "./components/layout/Logo";

function App() {

  const [fruit, setFruit] = useState("")
  const [result, setResult] = useState("")

  return (
    <Wrapper>
      <Logo/>
      <BubbleTop/>
      <BubbleBottom/>
      <Glass>
        <h1>Type your ingredients to see matching products</h1>
        <Form fruit={fruit} setFruit={setFruit} setResult={setResult}/>
        <div className={styles.combinations}>
        {result.length === 0 ? "" : result.map(function(result){
            return <div className = {styles.text}>{result}</div>
          })}
        </div>

      </Glass>
    </Wrapper>
  );
}

export default App;
